import React, { useState } from 'react';
import { Bus, Ticket } from '../../types';

interface TicketIssuerProps {
  bus: Bus;
  onTicketIssued?: (ticket: Ticket) => void;
  onClose?: () => void;
  className?: string;
}

interface TicketFormData {
  passengerPassNumber: string;
  boardingStop: string;
  fare: number;
  paymentMethod: 'pass' | 'cash' | 'digital' | 'card';
}

const TicketIssuer: React.FC<TicketIssuerProps> = ({
  bus,
  onTicketIssued,
  onClose,
  className = ''
}) => {
  const [formData, setFormData] = useState<TicketFormData>({
    passengerPassNumber: '',
    boardingStop: '',
    fare: 0,
    paymentMethod: 'pass'
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'fare' ? parseFloat(value) || 0 : value
    }));
    setError(null);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    setSuccess(null);

    try {
      const requestBody: any = {
        busId: bus._id,
        paymentMethod: formData.paymentMethod
      };

      // Add optional fields if provided
      if (formData.passengerPassNumber.trim()) {
        requestBody.passengerPassNumber = formData.passengerPassNumber.trim();
      }
      if (formData.boardingStop.trim()) {
        requestBody.boardingStop = formData.boardingStop.trim();
      }
      if (formData.fare > 0) {
        requestBody.fare = formData.fare;
      }

      const response = await fetch('http://localhost:5000/api/tickets/issue', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to issue ticket');
      }

      if (data.success && data.data?.ticket) {
        setSuccess(`Ticket issued successfully! Ticket ID: ${data.data.ticket.displayTicketId}`);

        // Reset form
        setFormData({
          passengerPassNumber: '',
          boardingStop: '',
          fare: 0,
          paymentMethod: 'pass'
        });

        // Notify parent component
        if (onTicketIssued) {
          onTicketIssued(data.data.ticket);
        }

        // Auto-close after 3 seconds
        setTimeout(() => {
          if (onClose) onClose();
        }, 3000);
      }

    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred while issuing the ticket');
    } finally {
      setIsLoading(false);
    }
  };

  const isFormValid = bus && bus._id;
  const occupancyPercentage = bus.occupancyPercentage || 0;
  const isBusFull = occupancyPercentage >= 100;

  return (
    <div className={`bg-white rounded-lg shadow-lg p-6 ${className}`}>
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900">Issue Ticket</h3>
        {onClose && (
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            ✕
          </button>
        )}
      </div>

      {/* Bus Information */}
      <div className="bg-gray-50 rounded-lg p-4 mb-4">
        <div className="flex items-center justify-between mb-2">
          <h4 className="font-medium text-gray-900">Bus {bus.busNumber}</h4>
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
            bus.status === 'Active' 
              ? 'bg-green-100 text-green-800'
              : 'bg-red-100 text-red-800'
          }`}>
            {bus.status}
          </span>
        </div>

        {typeof bus.routeId === 'object' && (
          <p className="text-sm text-gray-600 mb-2">
            Route: {bus.routeId.routeName} ({bus.routeId.routeNumber})
          </p>
        )}

        <div className="flex items-center space-x-4">
          <div className="flex-1">
            <div className="flex items-center justify-between text-xs mb-1">
              <span>Occupancy</span>
              <span>{occupancyPercentage}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className={`h-2 rounded-full ${
                  bus.occupancyStatus === 'green'
                    ? 'bg-green-500'
                    : bus.occupancyStatus === 'yellow'
                    ? 'bg-yellow-500'
                    : 'bg-red-500'
                }`}
                style={{ width: `${Math.min(occupancyPercentage, 100)}%` }}
              />
            </div>
          </div>
          <div className="text-xs text-gray-600">
            {bus.availableSeats || 0} seats available
          </div>
        </div>
      </div>

      {/* Warning if bus is full */}
      {isBusFull && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
          <div className="flex items-center">
            <span className="text-red-600 mr-2">⚠️</span>
            <p className="text-sm text-red-700">
              This bus is at full capacity. Issuing additional tickets may cause overcrowding.
            </p>
          </div>
        </div>
      )}

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
          <p className="text-sm text-red-700">{error}</p>
        </div>
      )}

      {/* Success Message */}
      {success && (
        <div className="bg-green-50 border border-green-200 rounded-lg p-3 mb-4">
          <p className="text-sm text-green-700">{success}</p>
        </div>
      )}

      {/* Ticket Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="paymentMethod" className="block text-sm font-medium text-gray-700 mb-1">
            Payment Method *
          </label>
          <select
            id="paymentMethod"
            name="paymentMethod"
            value={formData.paymentMethod}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            required
          >
            <option value="pass">Bus Pass</option>
            <option value="cash">Cash</option>
            <option value="digital">Digital Payment</option>
            <option value="card">Card</option>
          </select>
        </div>

        <div>
          <label htmlFor="passengerPassNumber" className="block text-sm font-medium text-gray-700 mb-1">
            Passenger Pass Number
          </label>
          <input
            type="text"
            id="passengerPassNumber"
            name="passengerPassNumber"
            value={formData.passengerPassNumber}
            onChange={handleInputChange}
            placeholder="Enter pass number (optional)"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <p className="text-xs text-gray-500 mt-1">
            Leave empty if passenger doesn't have a pass
          </p>
        </div>

        <div>
          <label htmlFor="boardingStop" className="block text-sm font-medium text-gray-700 mb-1">
            Boarding Stop
          </label>
          <input
            type="text"
            id="boardingStop"
            name="boardingStop"
            value={formData.boardingStop}
            onChange={handleInputChange}
            placeholder="Enter boarding stop name (optional)"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>

        <div>
          <label htmlFor="fare" className="block text-sm font-medium text-gray-700 mb-1">
            Fare Amount (₹)
          </label>
          <input
            type="number"
            id="fare"
            name="fare"
            value={formData.fare || ''}
            onChange={handleInputChange}
            placeholder="Enter fare amount (optional)"
            min="0"
            step="0.50"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>

        <div className="flex space-x-3 pt-4">
          <button
            type="submit"
            disabled={!isFormValid || isLoading || (isBusFull && formData.paymentMethod !== 'pass')}
            className={`flex-1 py-2 px-4 rounded-md font-medium transition-colors ${
              isFormValid && !isLoading && !(isBusFull && formData.paymentMethod !== 'pass')
                ? 'bg-blue-600 hover:bg-blue-700 text-white'
                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
            }`}
          >
            {isLoading ? (
              <span className="flex items-center justify-center">
                <svg className="animate-spin -ml-1 mr-3 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Issuing...
              </span>
            ) : (
              'Issue Ticket'
            )}
          </button>

          {onClose && (
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 transition-colors"
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      <div className="mt-4 pt-4 border-t border-gray-200">
        <p className="text-xs text-gray-500">
          * Digital tickets are issued for all passengers regardless of pass status for tracking and compliance purposes.
        </p>
      </div>
    </div>
  );
};

export default TicketIssuer;
